.news .body {
  position: relative;
  background: rgba(17, 34, 51, 0.75);
  border: 1px solid rgba(255, 255, 255, 0.5);
  margin-bottom: 1em;
}
.news .body::after {
  content: "";
  opacity: 0.1;
  background-size: 80px;
  background-position: top right;
  background-repeat: no-repeat;
  position: absolute;
  top: 10px;
  right: 10px;
  bottom: 0;
  left: 0;
}
.news .body .time,
.news .body .simple .title {
  color: #ccc;
}
